jasbro.game.world.customContent.effects..code:
int day = data.day;
int generatedNumber = (Util.getInt(0, 10) + day / 100) / 5;
int amountContests = Math.min(3, Math.max(1, generatedNumber));

//Add additional contests to this list (with day requirement if sensible)
List possibleContests = new ArrayList();
possibleContests.add("contestQuestMaidContestGeneration");
possibleContests.add("contestQuestSexContestGeneration");
if (data.day > 60) {
  possibleContests.add("contestQuestBartenderContestGeneration");
  possibleContests.add("contestQuestDancerContestGeneration");
}

int difficultyModifier = 0;
for (int i = 0; i < amountContests; i++) {
    difficulty = Math.min(3, Math.max(1, Util.getInt(-2, 2) + day / 100 + difficultyModifier));
    difficultyModifier -= difficulty;

    selectedContest = possibleContests.get(Util.getInt(0, possibleContests.size()));
    WorldEvent worldEvent = Jasbro.getInstance().getWorldEvents().get(selectedContest);        

    worldEvent.setInterpreter(this.interpreter);        
    worldEvent.execute();
}
