if (characterIds != null) {
    for (int i = 0; i < characterIds.length; i++) {
        String characterId = characterIds[i];
        for (int j = 0; j < data.auctionHouse.slaves.size(); j++) {
            if (characterId.equals(data.auctionHouse.slaves.get(j).base.id.toLowerCase())) {
                data.auctionHouse.slaves.remove(j);
                 j--;
            }
        }
        for (int j = 0; j < data.auctionHouse.trainers.size(); j++) {
            if (characterId.equals(data.auctionHouse.trainers.get(j).base.id.toLowerCase())) {
                data.auctionHouse.trainers.remove(j);
                j--;
            }
        }
    }
}
